import { addTodo } from "../Actions/action"

const initialstate={
    list:[]
}
// const [list,setList]




 export const Reducers=(state=initialstate,action)=>{
switch(action.type){
    case "ADD_TODO":
        const{id,data} = action.payload ;
            return {
                ...state,
                data: [
                    ...state.list,
                    {
                        id:id,
                    data:data
                }
            ]
            }
    //  return default  
    default: return state;

}

}
