const initialstate={
    list:[]
}
// const [list,setList]




const Reducer=(state=initialstate,action)=>{
switch(action.type){
    case "ADD_TODO":
        const{id,data} = action.payload ;
            return {
                ...state,
                list: [
                    ...state.list,
                    {
                        id:id,
                    data:data
                }
            ]
            }
    //  return default  
    default: return state;

}

}

export default Reducer
