import React, { useState } from "react";
import "./todo.css";
import { addTodo, delTodo, removeTodo } from "../Actions/action";
import { useDispatch, useSelector } from "react-redux";

export const Todo = () => {
  const [input, setInput] = useState("");
  constlist=useSelector((state)=>state.Reducer.list)
  const dispatch = useDispatch();

  return (
    <div className="main-div">
      <div className="child-div">
        <figure>
          <figcaption>Add your List here ✌ </figcaption>
        </figure>

        <div className="addItems">
          <input
            className="input-one"
            type="text"
            placeholder="✍ Add items"
            value={input}
            onChange={(e) => setInput(e.target.value)}
          />
          <i
            className="fa fa-plus add-btn"
            onClick={() => dispatch(addTodo(input), setInput(""))}
          ></i>
        </div>
        <div className="show-items">

            {
                list.map()
            }
          <div className="each-items">
            <h3>Test</h3>
            <i
              className="fa fa-trash add-btn"
              onClick={() => dispatch(delTodo(input), setInput(""))}
            ></i>
          </div>
        </div>
      </div>
    </div>
  );
};
